import { useState } from "react";
import styles from "./styles.module.css";

function ChatForm() {
  const [message, setMessage] = useState("");

  return (
    <div>
      <form action="">
        <input
          className={styles.textInput}
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
        />
      </form>
    </div>
  );
}

export default ChatForm;
